INSERT INTO `columns` VALUES (396, 104, 1, '【鹿ロース＆ももセール価格：兵庫県産】\r\n紹介：兵庫県で捕獲された鹿です。豊かな森で育った鹿なので最高の食味です。箱罠で捕獲し、電気ショッカーで失神させてから止差しです。解体も丁寧な処理移設です。\r\n\r\n①ロース\r\n1500円/キロ\r\n数量：10キロ\r\n②モモ\r\n700円/キロ\r\n数量：30キロ\r\n注文：どちらも２キロ単位でお願いします！\r\n温度：冷凍', '6sq8Q5nZntvTouvIbTo1WvHLY987MI73vX0NN2OQ.jpeg');
