INSERT INTO `ingredients` VALUES (98, 'insect', 'イナゴ', '昆虫', '0wf64e1n9y8VQWyquJyzCNKqpwwpFsLqTUakZhlB.jpeg', NULL, '通年', '長野県の伝統料理。加工品は通年あります。未加工は、夏のみ。猟師に注文します。', '大変美味しい。食感も良い。', '煮込み、唐揚げなど。', 100, 150, '匹', NULL);
