INSERT INTO `ingredients` VALUES (104, 'insect', 'フェモラーオオモモブトハムシ・幼虫', '昆虫', '', NULL, '冬', '外来種です。主に三重県で生息。くずの木に虫こぶを作ったところを採取。テレビなどで取り上げられ、人気上昇中。', '杏仁豆腐のような味わいとプチっとした食感。予想以上のレベルです。', '茹でる。揚げる。特に確立されいていない。', NULL, NULL, NULL, NULL);
